import React, { Component } from 'react';
import { myModels, myFavoriteModels, searchAllModels } from './services/apiService';
import ModelList from './components/modelList/modelList';
import Navigation from './components/navigation/navigation';
import 'materialize-css/dist/css/materialize.min.css';
import './App.css';

class App extends Component {
    constructor() {
        super();
        this.state = {
            isLoaded: false,
            models: {
                cursors: {},
                next: undefined,
                previous: null,
                results: []
            }
        };
    }
    componentDidMount() {
        myModels()
            .then((myModelsResponse) => {
                console.log(myModelsResponse);
                this.setState({
                    isLoaded: true,
                    models: myModelsResponse
                });
            })
    }
    render() {
        const { isLoaded, models } = this.state;
        function displayMyFavoriteModels() {
            this.setState({
                isLoaded: false,
            });
            myFavoriteModels()
                .then((myFavoritesResponse) => {
                    // console.log(myFavoritesResponse);
                    this.setState({
                        isLoaded: true,
                        models: myFavoritesResponse
                    });
                })
        }
        function displayModelsSearch(query) {
            this.setState({
                isLoaded: false,
            });
            searchAllModels(query)
                .then((searchResponse) => {
                    // console.log(searchResponse);
                    this.setState({
                        isLoaded: true,
                        models: searchResponse
                    });
                });
        }
        return (
            <div className="app-container">
                <Navigation onFavoriteClick={ displayMyFavoriteModels.bind(this) }></Navigation>
                <div className="divider"></div>
                <ModelList isLoaded = { isLoaded } models = { models.results }></ModelList>
            </div>
        );
    }
}

export default App;
