'use strict';

const API_ROOT = 'https://api.sketchfab.com/v3';
const AUTHORIZATION = 'Token 345e7ba8e00146f4882abe3af6a9f5e8';

export class ApiService {

    public async searchAllModels(query: string): Promise<any> {
        if (query && query.replace(/\s/g, '').length > 0) {
            return await this.fetchAPI(
                '/search?type=models&',
                `q=${query}`
            );
        }
    }

    private async fetchAPI(api: string, params: string): Promise<any> {
        return await fetch(
            API_ROOT + api + params,
            {
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': AUTHORIZATION
                }
            }
        );
    }

}
