import React, { Component } from 'react';
import Card from '../card/card';
import Modal from '../modal/modal';
import 'materialize-css/dist/css/materialize.min.css';
import './modelList.css';

class ModelList extends Component {
    constructor() {
        super();
        this.state = {
            selectedModel: undefined,
            isModalOpen: false,
            content: "Standard Content"
        };
    }
    openModelModal(model) {
        this.setState({
            selectedModel: model,
            isModalOpen: true,
            content: "New Content"
        });
    }
    render() {
        return (
            this.props.models.length > 0 && this.props.isLoaded
                ? (
                    <div className="gallery">
                        <div className="card-list row">
                            {this.props.models.map(result => (
                                <Card key={result.uid} model={result} showModelDetails={ this.openModelModal }></Card>
                            ))}
                        </div>
                        <Modal open={this.state.isModalOpen} content={this.state.content} />
                    </div>
                )
                : !this.props.isLoaded
                    ? (<div className="loading">Loading...</div>)
                    : (<div className="placeholder">The search didn't returned any model.</div>)
        );
    }
}

export default ModelList;
