import React, { Component } from 'react';
import M from "materialize-css";
import 'materialize-css/dist/css/materialize.min.css';
import './modal.css';

class Modal extends Component {
    constructor() {
        super();
        this.state = {
            instance: undefined
        };
    }
    componentDidMount() {
        const options = {
            onCloseEnd: () => {
                this.props.close();
            },
            inDuration: 250,
            outDuration: 250,
            opacity: 0.8,
            dismissible: true,
        };
        M.Modal.init(this.Modal, options);
        this.setState({
            instance: M.Modal.getInstance(this.Modal)
        });
    }
    componentWillReceiveProps(nProps) {
        console.log(nProps);
        if (nProps.open) {
            this.state.instance.open();
        }
    }
    render() {
        const model = this.props.model | {};
        console.log(model);
        return (
            <div>
                <div
                    ref={Modal => {
                        this.Modal = Modal;
                    }}
                    id="modal1"
                    className="modal"
                >
                    <div className="modal-content">
                        <h4>{this.props.model ? this.props.model.name : ''}</h4>
                        <iframe
                            src="https://sketchfab.com/models/9473ce8b5c6d4ae3ac056078aff50b28/embed"
                            title="{(this.props.model ? this.props.model.name : '') + '3DModel'}"
                            frameBorder="0"
                        />
                    </div>
                </div>
            </div>
        );
    }
}

export default Modal;
