import React, { Component } from 'react';
import { myModels, myFavoriteModels, searchAllModels } from './services/apiService';
import ModelList from './components/modelList/modelList';
import Navigation from './components/navigation/navigation';
import 'materialize-css/dist/css/materialize.min.css';
import './App.css';

class App extends Component {
    constructor() {
        super();
        this.state = {
            isLoaded: false,
            models: {
                cursors: {},
                next: undefined,
                previous: null,
                results: []
            }
        };
    }
    componentDidMount() {
        this.onyMyModelsClick();
    }
    onyMyModelsClick() {
        this.setState({
            isLoaded: false,
        });
        myModels()
            .then((myModelsResponse) => {
                console.log(myModelsResponse);
                this.setState({
                    isLoaded: true,
                    models: myModelsResponse
                });
            })
    }
    onMyFavoriteModelsClick() {
        this.setState({
            isLoaded: false,
        });
        myFavoriteModels()
            .then((myFavoritesResponse) => {
                // console.log(myFavoritesResponse);
                this.setState({
                    isLoaded: true,
                    models: myFavoritesResponse
                });
            })
    }
    displayModelsSearch(query) {
        this.setState({
            isLoaded: false,
        });
        searchAllModels(query)
            .then((searchResponse) => {
                // console.log(searchResponse);
                this.setState({
                    isLoaded: true,
                    models: searchResponse
                });
            });
    }
    render() {
        const { isLoaded, models } = this.state;

        return (
            <div className="app-container">
                <Navigation onyMyModelsClick={ this.onyMyModelsClick.bind(this) } onFavoriteClick={ this.onMyFavoriteModelsClick.bind(this) }></Navigation>
                <div className="divider"></div>
                <ModelList isLoaded = { isLoaded } models = { models.results }></ModelList>
            </div>
        );
    }
}

export default App;
