import React, { Component } from 'react';
import { myModels, myFavoriteModels, searchAllModels, fetchNewPage } from './services/apiService';
import ModelList from './components/modelList/modelList';
import Navigation from './components/navigation/navigation';
import Pagination from './components/pagination/pagination';
import Search from './components/search/search'
import 'materialize-css/dist/css/materialize.min.css';
import './App.css';

class App extends Component {
    constructor() {
        super();
        this.state = {
            currentAPI: 'mine',
            isLoaded: false,
            models: {
                cursors: {},
                next: undefined,
                previous: null,
                results: []
            }
        };
    }
    componentDidMount() {
        this.onMyModelsClick();
    }
    onMyModelsClick() {
        this.setState({
            currentAPI: 'mine',
            isLoaded: false,
        });
        myModels()
            .then((myModelsResponse) => {
                this.setState({
                    isLoaded: true,
                    models: myModelsResponse
                });
            })
    }
    onMyFavoriteModelsClick() {
        this.setState({
            currentAPI: 'favorite',
            isLoaded: false,
        });
        myFavoriteModels()
            .then((myFavoritesResponse) => {
                this.setState({
                    isLoaded: true,
                    models: myFavoritesResponse
                });
            })
    }
    displayModelsSearch(query) {
        this.setState({
            currentAPI: 'all',
            isLoaded: false,
        });
        searchAllModels(query)
            .then((searchResponse) => {
                this.setState({
                    isLoaded: true,
                    models: searchResponse
                });
            });
    }
    changePage(direction) {
        this.setState({
            isLoaded: false,
        });
        fetchNewPage(this.state.models[direction])
            .then((newPageResponse) => {
            this.setState({
                isLoaded: true,
                models: newPageResponse
            });
        });
    }
    render() {
        const { currentAPI, isLoaded, models } = this.state;
        return (
            <div className="app-container">
                <Navigation
                    currentAPI={ currentAPI }
                    onAllModelsClick={this.displayModelsSearch.bind(this)}
                    onMyModelsClick={this.onMyModelsClick.bind(this)}
                    onFavoriteClick={this.onMyFavoriteModelsClick.bind(this)}
                ></Navigation>
                <div className="divider"></div>
                <div className="row">
                    <Search></Search>
                    <Pagination models={models} changePage={this.changePage.bind(this)}></Pagination>
                </div>
                <ModelList isLoaded = { isLoaded } models = { models.results }></ModelList>
            </div>
        );
    }
}

export default App;
